name:
fun lineSpec(lineColor: Color, lineThickness: Dp = DefaultDimens.LINE_THICKNESS.dp, lineBackgroundShader: DynamicShader? = DynamicShaders.fromBrush(
        brush = Brush.verticalGradient(
            listOf(
                lineColor.copy(alpha = DefaultAlpha.LINE_BACKGROUND_SHADER_START),
                lineColor.copy(alpha = DefaultAlpha.LINE_BACKGROUND_SHADER_END),
            ),
        ),
    ), lineCap: StrokeCap = StrokeCap.Round, cubicStrength: Float = DefaultDimens.CUBIC_STRENGTH, point: Component? = null, pointSize: Dp = DefaultDimens.POINT_SIZE.dp): LineChart.LineSpec

